    @Test
    public void testJsonToInnerClass(){
        // error: JSONException: can't create non-static inner class instance.
        //只能使用一层内部类，再嵌套的话就不支持了
        String json = "{\n" +
                "    \"code\": 0,\n" +
                "    \"msg\": \"成功\",\n" +
                "    \"data\": {\n" +
                "        \"authUrl\": \"www.baidu.com\",\n" +
                "        \"authList\": [\n" +
                "            {\n" +
                "                \"authId\": \"001\",\n" +
                "                \"reportInfos\": [\n" +
                "                    {\n" +
                "                        \"reportId\": 222\n" +
                "                    }\n" +
                "                ]\n" +
                "            }\n" +
                "        ]\n" +
                "    }\n" +
                "}";
        WzTaxAuthResp wzTaxAuthResp = JSONObject.parseObject(json, WzTaxAuthResp.class);
        System.out.println("wzTaxAuthResp = " + wzTaxAuthResp);
    }

    @Test
    public void testJsonObject(){
        JSONObject jsonObject = new JSONObject();
        jsonObject.put("A","myName is Lihua");
        System.out.println("jsonObject = " + jsonObject);
        System.out.println("jsonObject1 = " + jsonObject.toString());
        System.out.println("jsonObject2 = " + jsonObject.toJSONString());
        System.out.println("jsonObject3 = " + JSON.toJSONString(jsonObject));
    }

    @Test
    public void testJsonNullValue(){
        JSONObject jsonObject = new JSONObject();
        jsonObject.put("KeyA","ValueA");
        jsonObject.put("KeyB",null);
        //输出value为null的key
        String s = JSON.toJSONString(jsonObject, SerializerFeature.WriteMapNullValue);
        System.out.println("s = " + s);
    }

    
